'    CASE ',
'        WHEN LAG("Cost") OVER (ORDER BY "Week") IS NULL THEN ''0'' || ''%''',
'        ELSE ',
'            CASE ',
'                WHEN ROUND((( "Cost" - LAG("Cost") OVER (ORDER BY "Week") ) / LAG("Cost") OVER (ORDER BY "Week") ) * 100, 2) >= 0 ',
'                THEN ''+'' || TO_CHAR(ROUND((( "Cost" - LAG("Cost") OVER (ORDER BY "Week") ) / LAG("Cost") OVER (ORDER BY "Week") ) * 100, 2), ''FM990.00'') || ''%''',
'                ELSE TO_CHAR(ROUND((( "Cost" - LAG("Cost") OVER (ORDER BY "Week") ) / LAG("Cost") OVER (ORDER BY "Week") ) * 100, 2), ''FM990.00'') || ''%''',
'            END',
'    END AS "% Difference"',
'FROM (',
'    SELECT ',
'        TRUNC(CHARGEPERIODEND,''IW'') AS "Week", ',
'        SUM(BILLEDCOST) AS "Cost"',
'    FROM FOCUS_REPORTS_PY',
'    WHERE CHARGEPERIODEND IS NOT NULL ',
'    AND TRUNC(CHARGEPERIODEND,''IW'') >= TRUNC(SYSDATE, ''IW'') - INTERVAL ''56'' DAY  -- Start 4 weeks ago',
