'      WHEN ROUND(p.AVAILABLE_AMOUNT, -2) = 0 THEN 0',
'      ELSE ROUND(p.AVAILABLE_AMOUNT, 1)',
'    END AS "AVAILABLE"',
'    FROM OCI_SUBSCRIPTIONS_PY p, OCI_SUBSCRIPTION_DETAILS d',
