,p_data_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT /*+ result_cache */',
'    "Month",',
'    "Cost",',
'    ROUND("NormalizedCost", 2) AS "Normalized Cost",',
'    CASE ',
'        WHEN LAG("NormalizedCost") OVER (ORDER BY "Month") IS NULL THEN ''0%''',
'        ELSE ',
'            CASE ',
'                WHEN ROUND((( "NormalizedCost" - LAG("NormalizedCost") OVER (ORDER BY "Month") ) / LAG("NormalizedCost") OVER (ORDER BY "Month") ) * 100, 2) >= 0 ',
'                THEN ''30 Day MoM: +'' || TO_CHAR(ROUND((( "NormalizedCost" - LAG("NormalizedCost") OVER (ORDER BY "Month") ) / LAG("NormalizedCost") OVER (ORDER BY "Month") ) * 100, 2), ''FM990.00'') || ''%''',
'                ELSE ''30 Day MoM: '' ||TO_CHAR(ROUND((( "NormalizedCost" - LAG("NormalizedCost") OVER (ORDER BY "Month") ) / LAG("NormalizedCost") OVER (ORDER BY "Month") ) * 100, 2), ''FM990.00'') || ''%''',
'            END',
'    END AS "% Difference"',
'FROM (',
'    SELECT ',
'        TO_CHAR(TRUNC(CHARGEPERIODEND,''MM''), ''YYYY-MM'') AS "Month", ',
'        SUM(BILLEDCOST) AS "Cost",',
'        (SUM(BILLEDCOST) / TO_NUMBER(TO_CHAR(LAST_DAY(TRUNC(CHARGEPERIODEND,''MM'')), ''DD''))) * 30 AS "NormalizedCost"',
'    FROM FOCUS_REPORTS_PY',
'    WHERE CHARGEPERIODEND IS NOT NULL ',
'      AND TRUNC(CHARGEPERIODEND,''MM'') >= TRUNC(SYSDATE, ''MM'') - INTERVAL ''6'' MONTH',
'      AND TRUNC(CHARGEPERIODEND,''MM'') < TRUNC(SYSDATE, ''MM'')',
'      AND OCI_COMPARTMENTID IN (',
'            SELECT trim(column_value)',
'            FROM TABLE(APEX_STRING.SPLIT(',
'                (SELECT DBMS_LOB.SUBSTR(VALUE, 4000)',
'                FROM (',
'                SELECT VALUE, ROW_NUMBER() OVER (ORDER BY WORKLOAD_NAME) AS rn',
'                FROM OCI_WORKLOADS',
'                )',
'                WHERE rn = 1',
'                ), '',''',
'            ))',
'            )',
'    GROUP BY TRUNC(CHARGEPERIODEND,''MM'')',
')',
'ORDER BY "Month";'))
