,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'  v_remaining_credits   NUMBER;',
'  v_projected_credits   NUMBER;',
'  v_percent_used        NUMBER;',
'BEGIN',
'  WITH credits_used AS (',
'    SELECT ',
'      COALESCE(SUM(BILLEDCOST), 0) / (7 * 24) AS hourly_rate',
'    FROM FOCUS_REPORTS_PY',
'    WHERE CHARGEPERIODEND >= SYSDATE - 7',
'      AND BILLINGACCOUNTID IN (',
'        SELECT column_value ',
'        FROM TABLE(apex_string.split(:P1_BILLINGACCOUNTID, '',''))',
'      )',
'  ),',
'  projected_credits AS (',
'    SELECT ',
'      COALESCE(hourly_rate * (TO_DATE(:P1_DATE_PICKER, ''MM/DD/YYYY'') - SYSDATE) * 24, 0) AS projected_amount',
'    FROM credits_used',
'  ),',
'  remaining_credits_raw AS (',
'    SELECT (',
'      COALESCE((',
'        SELECT SUM(s.COMMITED_CREDITS)',
'        FROM OCI_SUBSCRIPTION_DETAILS s',
'        WHERE CREDITS_CONSUMED = ''No'' AND CURRENCY = ''EUR''',
'      ), 0)',
'      -',
'      COALESCE((',
'        SELECT SUM(c.BILLEDCOST)',
'        FROM FOCUS_REPORTS_PY c',
'        WHERE TRUNC(CHARGEPERIODEND, ''HH'') >= TO_DATE(''2024-12-29 23:00'', ''YYYY-MM-DD HH24:MI'')',
'          AND BILLINGACCOUNTID IN (',
'            SELECT column_value ',
'            FROM TABLE(apex_string.split(:P1_BILLINGACCOUNTID, '',''))',
'          )',
'      ), 0)',
'    ) AS REMAINING_CREDITS',
'    FROM DUAL',
'  )',
'  SELECT ',
'    TO_CHAR(ROUND(REMAINING_CREDITS, 0), ''FM9G999G999G990''),',
'    TO_CHAR(ROUND(REMAINING_CREDITS - (SELECT projected_amount FROM projected_credits), 0), ''FM9G999G999G990''),',
'    ROUND(',
'      CASE ',
'        WHEN REMAINING_CREDITS = 0 THEN 0',
'        ELSE (SELECT projected_amount FROM projected_credits) / REMAINING_CREDITS * 100',
'      END, ',
'      1',
'    )',
'  INTO :P1_REMAINING_CREDITS, :P1_PROJECTED_CREDITS, :P1_PERCENT_USED',
'  FROM remaining_credits_raw;',
'END;',
''))
,p_process_clob_language=>'PLSQL'
