,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT ',
'    TRUNC(CHARGEPERIODSTART, ''MM'') AS "Month",',
'    BILLINGACCOUNTID, SUBACCOUNTNAME, INVOICEISSUER, REGION, BILLINGCURRENCY, SERVICECATEGORY, BILLEDCOST,',
'    RESOURCETYPE, SERVICENAME, SKUID, CHARGEDESCRIPTION, PRICINGUNIT, OCI_COMPARTMENTNAME, OCI_COMPARTMENTID, USAGEUNIT,',
