,p_query_type=>'SQL'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'WITH CompartmentData AS (',
'    -- Get all compartment paths and their IDs from both sources',
'    SELECT DISTINCT o.PATH AS "Compartment Path", o.COMPARTMENT_ID AS "CompartmentID"',
'    FROM OCI_COMPARTMENTS_PY o',
'    WHERE o.COMPARTMENT_ID NOT IN (',
'        ''ocid1.tenancy.oc1..aaaaaaaa5azptegxig27qdvycwt4demrwcdk2aia6youcjtx6guiprhc3vta'',',
'        ''ocid1.compartment.oc1..aaaaaaaamohvntqbkl4w6otoydob3ldl54anwf2jyqmhgafzhkev2a4g2epa'',',
'        ''ocid1.compartment.oc1..aaaaaaaacfxctxdpv3e64wrwg3mgyyeytsqko5hn47yr7rb3fpg7ssedm6xa''',
'    )',
')',
'SELECT ',
'    f.CHARGEDESCRIPTION, ',
'    f.USAGEUNIT,',
'    cd."Compartment Path",',
'    ROUND(SUM(',
'        CASE ',
'            -- If Pricing Metric contains ''month'', divide by days in month',
'            WHEN LOWER(f.USAGEUNIT) LIKE ''%month%'' ',
'            THEN f.USAGEQUANTITY * (730/24)',
'            -- Otherwise, divide by total hours in the month',
'            ELSE f.USAGEQUANTITY / 24',
'        END',
'    ), 0) AS "Usage"',
'FROM FOCUS_REPORTS_PY f',
'LEFT JOIN CompartmentData cd ',
'    ON f.OCI_COMPARTMENTID = cd."CompartmentID"  -- Join with CompartmentData to get "Compartment Path"',
'WHERE ',
'    (f.SUBACCOUNTNAME IN (SELECT COLUMN_VALUE FROM TABLE(apex_string.split(:P3_TENANT, '',''))) OR :P3_TENANT IS NULL)',
'    AND (f.BILLINGACCOUNTID IN (SELECT COLUMN_VALUE FROM TABLE(apex_string.split(:P3_SUBSCRIPTION_ID, '',''))) OR :P3_SUBSCRIPTION_ID IS NULL)',
'    AND (f.REGION IN (SELECT COLUMN_VALUE FROM TABLE(apex_string.split(:P3_REGION, '',''))) OR :P3_REGION IS NULL)',
'    AND (f.OCI_COMPARTMENTID IN (SELECT COLUMN_VALUE FROM TABLE(apex_string.split(:P3_SELECT_COMPARTMENT, '',''))) OR :P3_SELECT_COMPARTMENT IS NULL)',
'    AND (f.SERVICECATEGORY IN (SELECT COLUMN_VALUE FROM TABLE(apex_string.split(:P3_SERVICE_CATEGORY, '',''))) OR :P3_SERVICE_CATEGORY IS NULL)',
'    AND (f.SERVICENAME IN (SELECT COLUMN_VALUE FROM TABLE(apex_string.split(:P3_SERVICE_NAME, '',''))) OR :P3_SERVICE_NAME IS NULL)',
'    AND (f.CHARGEDESCRIPTION IN (SELECT COLUMN_VALUE FROM TABLE(apex_string.split(:P3_CHARGEDESCRIPTION, '',''))) OR :P3_CHARGEDESCRIPTION IS NULL)',
'    AND (f.RESOURCETYPE IN (SELECT COLUMN_VALUE FROM TABLE(apex_string.split(:P3_RESOURCE_TYPE, '',''))) OR :P3_RESOURCE_TYPE IS NULL)',
'    AND (f.RESOURCEID IN (SELECT COLUMN_VALUE FROM TABLE(apex_string.split(:P3_RESOURCE_NAME, '',''))) OR :P3_RESOURCE_NAME IS NULL)',
'    -- Date filtering',
'     AND CHARGEPERIODSTART >= TRUNC(SYSDATE - 3, ''DD'')',
'     AND CHARGEPERIODSTART < TRUNC(SYSDATE - 2, ''DD'')',
'    AND BILLEDCOST > 0',
'GROUP BY ',
'    f.CHARGEDESCRIPTION, ',
'    f.USAGEUNIT, ',
'    cd."Compartment Path";',
''))
,p_query_order_by_type=>'STATIC'
