,p_component_template_options=>'#DEFAULT#'
,p_plug_template=>2072724515482255512
,p_plug_display_sequence=>10
,p_query_type=>'SQL'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'WITH CompartmentData AS (',
'    -- Get all compartment paths and their IDs from both sources',
'    SELECT DISTINCT o.PATH AS "Compartment Path", o.COMPARTMENT_ID AS "CompartmentID"',
'    FROM OCI_COMPARTMENTS_PY o',
'    WHERE o.COMPARTMENT_ID NOT IN (',
'        ''ocid1.tenancy.oc1..aaaaaaaa5azptegxig27qdvycwt4demrwcdk2aia6youcjtx6guiprhc3vta'',',
'        ''ocid1.compartment.oc1..aaaaaaaamohvntqbkl4w6otoydob3ldl54anwf2jyqmhgafzhkev2a4g2epa'',',
'        ''ocid1.compartment.oc1..aaaaaaaacfxctxdpv3e64wrwg3mgyyeytsqko5hn47yr7rb3fpg7ssedm6xa'',',
'        ''ocid1.tenancy.oc1..aaaaaaaaqr55dbhrptqbdzghhochnmaarikn6h2chep53nugzd5tygphvemq''',
'    )',
')',
'SELECT ',
'    f.CHARGEDESCRIPTION, ',
'    f.USAGEUNIT,',
'    cd."Compartment Path",',
'    ROUND(SUM(',
'        CASE ',
'            -- If Pricing Metric contains ''month'', divide by days in month',
'            WHEN LOWER(f.USAGEUNIT) LIKE ''%month%'' ',
'            THEN f.USAGEQUANTITY ',
'            -- Otherwise, divide by total hours in the month',
'            ELSE f.USAGEQUANTITY / 24',
'        END',
'    ), 0) AS "Usage"',
'FROM FOCUS_REPORTS_PY f',
'LEFT JOIN CompartmentData cd ',
'    ON f.OCI_COMPARTMENTID = cd."CompartmentID"  -- Join with CompartmentData to get "Compartment Path"',
'WHERE ',
'    (f.SUBACCOUNTNAME IN (SELECT COLUMN_VALUE FROM TABLE(apex_string.split(:P4_TENANT, '',''))) OR :P4_TENANT IS NULL)',
'    AND (f.BILLINGACCOUNTID IN (SELECT COLUMN_VALUE FROM TABLE(apex_string.split(:P4_SUBSCRIPTION_ID, '',''))) OR :P4_SUBSCRIPTION_ID IS NULL)',
'    AND (f.REGION IN (SELECT COLUMN_VALUE FROM TABLE(apex_string.split(:P4_REGION, '',''))) OR :P4_REGION IS NULL)',
'    AND (f.OCI_COMPARTMENTID IN (SELECT COLUMN_VALUE FROM TABLE(apex_string.split(:P4_SELECT_COMPARTMENT, '',''))) OR :P4_SELECT_COMPARTMENT IS NULL)',
'    AND (f.SERVICECATEGORY IN (SELECT COLUMN_VALUE FROM TABLE(apex_string.split(:P4_SERVICE_CATEGORY, '',''))) OR :P4_SERVICE_CATEGORY IS NULL)',
'    AND (f.SERVICENAME IN (SELECT COLUMN_VALUE FROM TABLE(apex_string.split(:P4_SERVICE_NAME, '',''))) OR :P4_SERVICE_NAME IS NULL)',
'    AND (f.CHARGEDESCRIPTION IN (SELECT COLUMN_VALUE FROM TABLE(apex_string.split(:P4_CHARGEDESCRIPTION, '',''))) OR :P4_CHARGEDESCRIPTION IS NULL)',
'    AND (f.RESOURCETYPE IN (SELECT COLUMN_VALUE FROM TABLE(apex_string.split(:P4_RESOURCE_TYPE, '',''))) OR :P4_RESOURCE_TYPE IS NULL)',
'    AND (f.RESOURCEID IN (SELECT COLUMN_VALUE FROM TABLE(apex_string.split(:P4_RESOURCE_NAME, '',''))) OR :P4_RESOURCE_NAME IS NULL)',
'    -- Date filtering',
'     AND CHARGEPERIODSTART >= TRUNC(SYSDATE - 3, ''DD'')',
'     AND CHARGEPERIODSTART < TRUNC(SYSDATE - 2, ''DD'')',
'    AND BILLEDCOST > 0',
'GROUP BY ',
'    f.CHARGEDESCRIPTION, ',
'    f.USAGEUNIT, ',
'    cd."Compartment Path";',
''))
,p_query_order_by_type=>'STATIC'
,p_query_order_by=>'"Compartment Path",CHARGEDESCRIPTION'
,p_lazy_loading=>false
,p_plug_source_type=>'NATIVE_CARDS'
,p_plug_query_num_rows=>12
,p_plug_query_num_rows_type=>'SET'
,p_show_total_row_count=>true
,p_ai_enabled=>false
);
